import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { issuesAPI } from '../services/api';
import { Monitor, Lightbulb, Fan, Tv, X } from 'lucide-react';
import toast from 'react-hot-toast';

const IssueModal = ({ lab, onClose, onSubmit }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    equipmentType: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);

  const equipmentOptions = [
    {
      value: 'computers',
      label: 'Computers',
      icon: Monitor,
      available: lab.equipment.computers > 0
    },
    {
      value: 'lights',
      label: 'Lights',
      icon: Lightbulb,
      available: lab.equipment.lights > 0
    },
    {
      value: 'fans',
      label: 'Fans',
      icon: Fan,
      available: lab.equipment.fans > 0
    },
    {
      value: 'smartBoard',
      label: 'Smart Board',
      icon: Tv,
      available: lab.equipment.smartBoard
    }
  ];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.equipmentType || !formData.description.trim()) {
      toast.error('Please fill in all fields');
      return;
    }

    setLoading(true);

    try {
      await issuesAPI.create({
        labId: lab._id,
        equipmentType: formData.equipmentType,
        description: formData.description.trim()
      });

      toast.success('Issue reported successfully!');
      onSubmit();
    } catch (error) {
      console.error('Error reporting issue:', error);
      toast.error('Failed to report issue. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">Report Issue</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Content */}
        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Lab Info */}
          <div className="bg-gray-50 rounded-lg p-4">
            <h3 className="font-medium text-gray-900">{lab.labName}</h3>
            <p className="text-sm text-gray-600 capitalize">{lab.type}</p>
            <p className="text-xs text-gray-500 mt-1">
              Reported by: {user?.name}
            </p>
          </div>

          {/* Equipment Selection */}
          <div>
            <label className="label">Faulty Equipment</label>
            <div className="grid grid-cols-2 gap-3">
              {equipmentOptions
                .filter(option => option.available)
                .map((option) => {
                  const Icon = option.icon;
                  return (
                    <button
                      key={option.value}
                      type="button"
                      onClick={() => setFormData({ ...formData, equipmentType: option.value })}
                      className={`flex items-center p-3 border rounded-lg transition-colors ${
                        formData.equipmentType === option.value
                          ? 'border-primary-500 bg-primary-50 text-primary-700'
                          : 'border-gray-300 bg-white text-gray-700 hover:bg-gray-50'
                      }`}
                    >
                      <Icon className="h-5 w-5 mr-2" />
                      {option.label}
                    </button>
                  );
                })}
            </div>
            {!formData.equipmentType && (
              <p className="mt-1 text-sm text-red-600">Please select the faulty equipment</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label htmlFor="description" className="label">
              Problem Description
            </label>
            <textarea
              id="description"
              name="description"
              rows={4}
              className="input w-full resize-none"
              placeholder="Describe the issue in detail..."
              value={formData.description}
              onChange={handleChange}
              required
            />
            <p className="mt-1 text-sm text-gray-500">
              Please provide as much detail as possible to help resolve the issue quickly.
            </p>
          </div>

          {/* Actions */}
          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 btn btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="flex-1 btn btn-danger"
              disabled={loading || !formData.equipmentType || !formData.description.trim()}
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Reporting...
                </div>
              ) : (
                'Report Issue'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default IssueModal;
