import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { labsAPI, issuesAPI } from '../services/api';
import { AlertCircle, CheckCircle, Clock, TrendingUp, BookOpen, Users } from 'lucide-react';
import toast from 'react-hot-toast';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalLabs: 0,
    totalIssues: 0,
    resolvedIssues: 0,
    pendingIssues: 0
  });
  const [recentIssues, setRecentIssues] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [labsResponse, issuesResponse] = await Promise.all([
        labsAPI.getAll(),
        issuesAPI.getAll()
      ]);

      const labs = labsResponse.data;
      const issues = issuesResponse.data;

      // Filter issues reported by current user
      const userIssues = issues.filter(issue => issue.reportedBy._id === user._id);

      setStats({
        totalLabs: labs.length,
        totalIssues: userIssues.length,
        resolvedIssues: userIssues.filter(issue => issue.status === 'resolved').length,
        pendingIssues: userIssues.filter(issue => issue.status === 'open').length
      });

      // Get recent issues (last 5)
      setRecentIssues(userIssues.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, icon: Icon, color, bgColor }) => (
    <div className={`card p-6 ${bgColor}`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className={`text-3xl font-bold ${color}`}>{value}</p>
        </div>
        <Icon className={`h-8 w-8 ${color}`} />
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="bg-white rounded-xl shadow-lg p-6 border-l-4 border-primary-500">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">
          Welcome back, {user?.name}!
        </h1>
        <p className="text-gray-600">
          Here's an overview of your lab and classroom issue reports.
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Labs"
          value={stats.totalLabs}
          icon={BookOpen}
          color="text-blue-600"
          bgColor="bg-blue-50"
        />
        <StatCard
          title="My Issues"
          value={stats.totalIssues}
          icon={AlertCircle}
          color="text-orange-600"
          bgColor="bg-orange-50"
        />
        <StatCard
          title="Resolved"
          value={stats.resolvedIssues}
          icon={CheckCircle}
          color="text-green-600"
          bgColor="bg-green-50"
        />
        <StatCard
          title="Pending"
          value={stats.pendingIssues}
          icon={Clock}
          color="text-red-600"
          bgColor="bg-red-50"
        />
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Issues */}
        <div className="card p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Recent Issues</h2>
            <Link
              to="/labs"
              className="text-primary-600 hover:text-primary-700 text-sm font-medium"
            >
              View all labs â†’
            </Link>
          </div>

          {recentIssues.length > 0 ? (
            <div className="space-y-3">
              {recentIssues.map((issue) => (
                <div key={issue._id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex-1">
                    <p className="font-medium text-gray-900">{issue.labId.labName}</p>
                    <p className="text-sm text-gray-600 capitalize">{issue.equipmentType}</p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span
                      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        issue.status === 'resolved'
                          ? 'bg-green-100 text-green-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}
                    >
                      {issue.status}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <AlertCircle className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No issues reported</h3>
              <p className="mt-1 text-sm text-gray-500">
                Get started by reporting an issue in a lab or classroom.
              </p>
              <div className="mt-6">
                <Link
                  to="/labs"
                  className="btn btn-primary"
                >
                  Browse Labs
                </Link>
              </div>
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="card p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
          <div className="space-y-3">
            <Link
              to="/labs"
              className="flex items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors"
            >
              <AlertCircle className="h-6 w-6 text-primary-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Report an Issue</p>
                <p className="text-sm text-gray-600">Browse labs and report equipment problems</p>
              </div>
            </Link>

            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <TrendingUp className="h-6 w-6 text-gray-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Issue Statistics</p>
                <p className="text-sm text-gray-600">
                  {stats.resolvedIssues} resolved, {stats.pendingIssues} pending
                </p>
              </div>
            </div>

            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <Users className="h-6 w-6 text-gray-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Account</p>
                <p className="text-sm text-gray-600">Student account - {user?.email}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
