import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { labsAPI } from '../services/api';
import { Monitor, Lightbulb, Fan, Tv, AlertTriangle, Plus, Search, Filter } from 'lucide-react';
import toast from 'react-hot-toast';
import IssueModal from '../components/IssueModal';

const LabsPage = () => {
  const { user, isAdmin } = useAuth();
  const [labs, setLabs] = useState([]);
  const [filteredLabs, setFilteredLabs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [typeFilter, setTypeFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedLab, setSelectedLab] = useState(null);
  const [showIssueModal, setShowIssueModal] = useState(false);

  useEffect(() => {
    fetchLabs();
  }, []);

  useEffect(() => {
    filterLabs();
  }, [labs, searchTerm, typeFilter, statusFilter]);

  const fetchLabs = async () => {
    try {
      const response = await labsAPI.getAll();
      setLabs(response.data);
    } catch (error) {
      console.error('Error fetching labs:', error);
      toast.error('Failed to load labs');
    } finally {
      setLoading(false);
    }
  };

  const filterLabs = () => {
    let filtered = labs;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(lab =>
        lab.labName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Type filter
    if (typeFilter !== 'all') {
      filtered = filtered.filter(lab => lab.type === typeFilter);
    }

    // Status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(lab => lab.status === statusFilter);
    }

    setFilteredLabs(filtered);
  };

  const handleReportIssue = (lab) => {
    setSelectedLab(lab);
    setShowIssueModal(true);
  };

  const handleIssueSubmitted = () => {
    setShowIssueModal(false);
    setSelectedLab(null);
    fetchLabs(); // Refresh labs to show updated status
  };

  const getEquipmentIcon = (type) => {
    switch (type) {
      case 'computers':
        return Monitor;
      case 'lights':
        return Lightbulb;
      case 'fans':
        return Fan;
      case 'smartBoard':
        return Tv;
      default:
        return Monitor;
    }
  };

  const LabCard = ({ lab }) => {
    const isIssue = lab.status === 'issue';

    return (
      <div className={`card p-6 transition-all duration-200 hover:shadow-xl ${
        isIssue
          ? 'border-red-200 bg-red-50'
          : 'border-green-200 bg-green-50'
      }`}>
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div>
            <h3 className="text-lg font-semibold text-gray-900">{lab.labName}</h3>
            <p className="text-sm text-gray-600 capitalize">{lab.type}</p>
          </div>
          <div className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
            isIssue
              ? 'bg-red-100 text-red-800'
              : 'bg-green-100 text-green-800'
          }`}>
            {isIssue ? 'Issue Reported' : 'Normal'}
          </div>
        </div>

        {/* Equipment */}
        <div className="mb-4">
          <h4 className="text-sm font-medium text-gray-700 mb-2">Equipment:</h4>
          <div className="grid grid-cols-2 gap-2 text-sm">
            {lab.equipment.computers > 0 && (
              <div className="flex items-center text-gray-600">
                <Monitor className="h-4 w-4 mr-1" />
                {lab.equipment.computers} Computer{lab.equipment.computers !== 1 ? 's' : ''}
              </div>
            )}
            {lab.equipment.lights > 0 && (
              <div className="flex items-center text-gray-600">
                <Lightbulb className="h-4 w-4 mr-1" />
                {lab.equipment.lights} Light{lab.equipment.lights !== 1 ? 's' : ''}
              </div>
            )}
            {lab.equipment.fans > 0 && (
              <div className="flex items-center text-gray-600">
                <Fan className="h-4 w-4 mr-1" />
                {lab.equipment.fans} Fan{lab.equipment.fans !== 1 ? 's' : ''}
              </div>
            )}
            {lab.equipment.smartBoard && (
              <div className="flex items-center text-gray-600">
                <Tv className="h-4 w-4 mr-1" />
                Smart Board
              </div>
            )}
          </div>
        </div>

        {/* Actions */}
        <div className="flex space-x-2">
          {!isAdmin() && (
            <button
              onClick={() => handleReportIssue(lab)}
              className="flex-1 btn btn-danger text-sm"
              disabled={isIssue}
            >
              {isIssue ? 'Issue Reported' : 'Report Issue'}
            </button>
          )}
          {isAdmin() && (
            <button className="flex-1 btn btn-secondary text-sm">
              Manage
            </button>
          )}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Labs & Classrooms</h1>
          <p className="text-gray-600 mt-1">
            {isAdmin()
              ? 'Manage all labs and classrooms'
              : 'Browse and report issues in labs and classrooms'
            }
          </p>
        </div>
        {isAdmin() && (
          <button className="mt-4 sm:mt-0 btn btn-primary">
            <Plus className="h-4 w-4 mr-2" />
            Add New Lab
          </button>
        )}
      </div>

      {/* Filters */}
      <div className="card p-4">
        <div className="flex flex-col sm:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search labs..."
                className="input pl-10 w-full"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>

          {/* Type Filter */}
          <div className="sm:w-48">
            <select
              className="input w-full"
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
            >
              <option value="all">All Types</option>
              <option value="Lab">Labs</option>
              <option value="Classroom">Classrooms</option>
            </select>
          </div>

          {/* Status Filter */}
          <div className="sm:w-48">
            <select
              className="input w-full"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">All Status</option>
              <option value="normal">Normal</option>
              <option value="issue">Has Issues</option>
            </select>
          </div>
        </div>
      </div>

      {/* Labs Grid */}
      {filteredLabs.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredLabs.map((lab) => (
            <LabCard key={lab._id} lab={lab} />
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <AlertTriangle className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No labs found</h3>
          <p className="mt-1 text-sm text-gray-500">
            {searchTerm || typeFilter !== 'all' || statusFilter !== 'all'
              ? 'Try adjusting your search or filter criteria.'
              : 'No labs have been added yet.'
            }
          </p>
        </div>
      )}

      {/* Issue Modal */}
      {showIssueModal && selectedLab && (
        <IssueModal
          lab={selectedLab}
          onClose={() => setShowIssueModal(false)}
          onSubmit={handleIssueSubmitted}
        />
      )}
    </div>
  );
};

export default LabsPage;
