import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { labsAPI, issuesAPI } from '../services/api';
import { AlertCircle, CheckCircle, Clock, TrendingUp, BookOpen, Users, Settings, X, Eye, Check } from 'lucide-react';
import toast from 'react-hot-toast';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalLabs: 0,
    totalIssues: 0,
    resolvedIssues: 0,
    pendingIssues: 0,
    labsWithIssues: 0
  });
  const [recentIssues, setRecentIssues] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [labsResponse, issuesResponse] = await Promise.all([
        labsAPI.getAll(),
        issuesAPI.getAll()
      ]);

      const labs = labsResponse.data;
      const issues = issuesResponse.data;

      setStats({
        totalLabs: labs.length,
        totalIssues: issues.length,
        resolvedIssues: issues.filter(issue => issue.status === 'resolved').length,
        pendingIssues: issues.filter(issue => issue.status === 'open').length,
        labsWithIssues: labs.filter(lab => lab.status === 'issue').length
      });

      // Get recent issues (last 5)
      setRecentIssues(issues.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const handleResolveIssue = async (issueId) => {
    try {
      await issuesAPI.resolve(issueId);
      toast.success('Issue resolved successfully');
      fetchDashboardData(); // Refresh data
    } catch (error) {
      console.error('Error resolving issue:', error);
      toast.error('Failed to resolve issue');
    }
  };

  const StatCard = ({ title, value, icon: Icon, color, bgColor }) => (
    <div className={`card p-6 ${bgColor}`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className={`text-3xl font-bold ${color}`}>{value}</p>
        </div>
        <Icon className={`h-8 w-8 ${color}`} />
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="bg-white rounded-xl shadow-lg p-6 border-l-4 border-primary-500">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">
          Admin Dashboard
        </h1>
        <p className="text-gray-600">
          Manage labs, classrooms, and resolve reported issues.
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Labs"
          value={stats.totalLabs}
          icon={BookOpen}
          color="text-blue-600"
          bgColor="bg-blue-50"
        />
        <StatCard
          title="All Issues"
          value={stats.totalIssues}
          icon={AlertCircle}
          color="text-orange-600"
          bgColor="bg-orange-50"
        />
        <StatCard
          title="Resolved"
          value={stats.resolvedIssues}
          icon={CheckCircle}
          color="text-green-600"
          bgColor="bg-green-50"
        />
        <StatCard
          title="Labs with Issues"
          value={stats.labsWithIssues}
          icon={Clock}
          color="text-red-600"
          bgColor="bg-red-50"
        />
      </div>

      {/* Management Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Issues */}
        <div className="card p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Recent Issues</h2>
            <Link
              to="/issues"
              className="text-primary-600 hover:text-primary-700 text-sm font-medium"
            >
              View all issues â†’
            </Link>
          </div>

          {recentIssues.length > 0 ? (
            <div className="space-y-3">
              {recentIssues.map((issue) => (
                <div key={issue._id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex-1">
                    <p className="font-medium text-gray-900">{issue.labId.labName}</p>
                    <p className="text-sm text-gray-600 capitalize">
                      {issue.equipmentType} - {issue.reportedBy.name}
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span
                      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        issue.status === 'resolved'
                          ? 'bg-green-100 text-green-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}
                    >
                      {issue.status}
                    </span>
                    {issue.status === 'open' && (
                      <button
                        onClick={() => handleResolveIssue(issue._id)}
                        className="btn btn-success text-xs px-2 py-1"
                      >
                        Resolve
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <CheckCircle className="mx-auto h-12 w-12 text-green-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No issues reported</h3>
              <p className="mt-1 text-sm text-gray-500">
                All labs and classrooms are functioning normally.
              </p>
            </div>
          )}
        </div>

        {/* Admin Actions */}
        <div className="card p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Admin Actions</h2>
          <div className="space-y-3">
            <Link
              to="/labs"
              className="flex items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors"
            >
              <BookOpen className="h-6 w-6 text-primary-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Manage Labs</p>
                <p className="text-sm text-gray-600">Add, edit, or remove labs and classrooms</p>
              </div>
            </Link>

            <Link
              to="/issues"
              className="flex items-center p-4 bg-orange-50 rounded-lg hover:bg-orange-100 transition-colors"
            >
              <AlertCircle className="h-6 w-6 text-orange-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Issue Management</p>
                <p className="text-sm text-gray-600">View and resolve all reported issues</p>
              </div>
            </Link>

            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <TrendingUp className="h-6 w-6 text-gray-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">System Statistics</p>
                <p className="text-sm text-gray-600">
                  {stats.pendingIssues} pending, {stats.resolvedIssues} resolved issues
                </p>
              </div>
            </div>

            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <Users className="h-6 w-6 text-gray-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Admin Account</p>
                <p className="text-sm text-gray-600">Administrator - {user?.email}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* System Status */}
      <div className="card p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">System Status</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-12 h-12 bg-green-100 rounded-full mb-2">
              <CheckCircle className="h-6 w-6 text-green-600" />
            </div>
            <p className="text-sm font-medium text-gray-900">System Health</p>
            <p className="text-xs text-gray-500">All systems operational</p>
          </div>
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-12 h-12 bg-blue-100 rounded-full mb-2">
              <BookOpen className="h-6 w-6 text-blue-600" />
            </div>
            <p className="text-sm font-medium text-gray-900">Active Labs</p>
            <p className="text-xs text-gray-500">{stats.totalLabs} labs registered</p>
          </div>
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-12 h-12 bg-orange-100 rounded-full mb-2">
              <Clock className="h-6 w-6 text-orange-600" />
            </div>
            <p className="text-sm font-medium text-gray-900">Pending Issues</p>
            <p className="text-xs text-gray-500">{stats.pendingIssues} issues awaiting resolution</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
