import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { labsAPI } from '../services/api';
import { Monitor, Lightbulb, Fan, Tv, AlertTriangle, Plus, Search, Filter, CheckCircle, Settings, BookOpen, AlertCircle } from 'lucide-react';
import toast from 'react-hot-toast';
import IssueModal from '../components/IssueModal';

const LabsPage = () => {
  const { user, isAdmin } = useAuth();
  const [labs, setLabs] = useState([]);
  const [filteredLabs, setFilteredLabs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [typeFilter, setTypeFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedLab, setSelectedLab] = useState(null);
  const [showIssueModal, setShowIssueModal] = useState(false);

  useEffect(() => {
    fetchLabs();
  }, []);

  useEffect(() => {
    filterLabs();
  }, [labs, searchTerm, typeFilter, statusFilter]);

  const fetchLabs = async () => {
    try {
      const response = await labsAPI.getAll();
      setLabs(response.data);
    } catch (error) {
      console.error('Error fetching labs:', error);
      toast.error('Failed to load labs');
    } finally {
      setLoading(false);
    }
  };

  const filterLabs = () => {
    let filtered = labs;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(lab =>
        lab.labName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Type filter
    if (typeFilter !== 'all') {
      filtered = filtered.filter(lab => lab.type === typeFilter);
    }

    // Status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(lab => lab.status === statusFilter);
    }

    setFilteredLabs(filtered);
  };

  const handleReportIssue = (lab) => {
    setSelectedLab(lab);
    setShowIssueModal(true);
  };

  const handleIssueSubmitted = () => {
    setShowIssueModal(false);
    setSelectedLab(null);
    fetchLabs(); // Refresh labs to show updated status
  };

  const getEquipmentIcon = (type) => {
    switch (type) {
      case 'computers':
        return Monitor;
      case 'lights':
        return Lightbulb;
      case 'fans':
        return Fan;
      case 'smartBoard':
        return Tv;
      default:
        return Monitor;
    }
  };

  const LabCard = ({ lab }) => {
    const isIssue = lab.status === 'issue';

    return (
      <div className={`card group hover-lift ${isIssue ? 'ring-2 ring-danger-500/50' : 'ring-2 ring-transparent hover:ring-primary-500/30'} transition-all duration-300`}>
        {/* Status indicator */}
        <div className={`absolute top-4 right-4 w-3 h-3 rounded-full ${isIssue ? 'bg-danger-500 animate-pulse' : 'bg-success-500'} shadow-lg`}></div>

        {/* Header */}
        <div className="flex items-start justify-between mb-6">
          <div className="flex items-center space-x-3">
            <div className={`p-3 rounded-xl ${isIssue ? 'bg-danger-100 dark:bg-danger-900/20' : 'bg-primary-100 dark:bg-primary-900/20'} group-hover:scale-110 transition-transform duration-300`}>
              <BookOpen className={`h-6 w-6 ${isIssue ? 'text-danger-600' : 'text-primary-600'}`} />
            </div>
            <div>
              <h3 className="text-lg font-bold text-gray-900 dark:text-white group-hover:text-primary-700 dark:group-hover:text-primary-300 transition-colors">
                {lab.labName}
              </h3>
              <p className="text-sm text-gray-600 dark:text-gray-400 capitalize flex items-center">
                <div className="w-2 h-2 bg-primary-500 rounded-full mr-2"></div>
                {lab.type}
              </p>
            </div>
          </div>
          <div className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold shadow-sm ${
            isIssue
              ? 'bg-danger-100 dark:bg-danger-900/20 text-danger-700 dark:text-danger-400'
              : 'bg-success-100 dark:bg-success-900/20 text-success-700 dark:text-success-400'
          }`}>
            {isIssue ? (
              <>
                <AlertTriangle className="w-3 h-3 mr-1" />
                Issue Reported
              </>
            ) : (
              <>
                <CheckCircle className="w-3 h-3 mr-1" />
                Normal
              </>
            )}
          </div>
        </div>

        {/* Equipment Grid */}
        <div className="mb-6">
          <h4 className="text-sm font-semibold text-gray-700 dark:text-gray-300 mb-3 flex items-center">
            <Monitor className="w-4 h-4 mr-2 text-primary-500" />
            Equipment Available
          </h4>
          <div className="grid grid-cols-2 gap-3">
            {lab.equipment.computers > 0 && (
              <div className="flex items-center p-2 bg-gray-50 dark:bg-dark-800 rounded-lg hover:bg-primary-50 dark:hover:bg-primary-900/20 transition-colors">
                <div className="p-1.5 bg-blue-100 dark:bg-blue-900/20 rounded-lg mr-3">
                  <Monitor className="h-3 w-3 text-blue-600" />
                </div>
                <div>
                  <p className="text-xs font-medium text-gray-900 dark:text-white">{lab.equipment.computers}</p>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Computer{lab.equipment.computers !== 1 ? 's' : ''}</p>
                </div>
              </div>
            )}
            {lab.equipment.lights > 0 && (
              <div className="flex items-center p-2 bg-gray-50 dark:bg-dark-800 rounded-lg hover:bg-primary-50 dark:hover:bg-primary-900/20 transition-colors">
                <div className="p-1.5 bg-yellow-100 dark:bg-yellow-900/20 rounded-lg mr-3">
                  <Lightbulb className="h-3 w-3 text-yellow-600" />
                </div>
                <div>
                  <p className="text-xs font-medium text-gray-900 dark:text-white">{lab.equipment.lights}</p>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Light{lab.equipment.lights !== 1 ? 's' : ''}</p>
                </div>
              </div>
            )}
            {lab.equipment.fans > 0 && (
              <div className="flex items-center p-2 bg-gray-50 dark:bg-dark-800 rounded-lg hover:bg-primary-50 dark:hover:bg-primary-900/20 transition-colors">
                <div className="p-1.5 bg-green-100 dark:bg-green-900/20 rounded-lg mr-3">
                  <Fan className="h-3 w-3 text-green-600" />
                </div>
                <div>
                  <p className="text-xs font-medium text-gray-900 dark:text-white">{lab.equipment.fans}</p>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Fan{lab.equipment.fans !== 1 ? 's' : ''}</p>
                </div>
              </div>
            )}
            {lab.equipment.smartBoard && (
              <div className="flex items-center p-2 bg-gray-50 dark:bg-dark-800 rounded-lg hover:bg-primary-50 dark:hover:bg-primary-900/20 transition-colors">
                <div className="p-1.5 bg-purple-100 dark:bg-purple-900/20 rounded-lg mr-3">
                  <Tv className="h-3 w-3 text-purple-600" />
                </div>
                <div>
                  <p className="text-xs font-medium text-gray-900 dark:text-white">1</p>
                  <p className="text-xs text-gray-600 dark:text-gray-400">Smart Board</p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Actions */}
        <div className="flex space-x-3">
          {!isAdmin() && (
            <button
              onClick={() => handleReportIssue(lab)}
              className={`flex-1 btn ${isIssue ? 'btn-outline' : 'btn-danger'} text-sm font-semibold hover:shadow-lg transition-all duration-300`}
              disabled={isIssue}
            >
              {isIssue ? (
                <>
                  <AlertTriangle className="w-4 h-4 mr-2" />
                  Issue Reported
                </>
              ) : (
                <>
                  <AlertCircle className="w-4 h-4 mr-2" />
                  Report Issue
                </>
              )}
            </button>
          )}
          {isAdmin() && (
            <button className="flex-1 btn-secondary text-sm font-semibold hover:shadow-md">
              <Settings className="w-4 h-4 mr-2" />
              Manage Lab
            </button>
          )}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8 fade-in">
      {/* Header */}
      <div className="card-elevated p-8 relative overflow-hidden">
        <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-primary-400/10 to-primary-600/10 rounded-full blur-2xl"></div>
        <div className="relative z-10 flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <div className="flex items-center space-x-4 mb-4 sm:mb-0">
            <div className="p-3 bg-gradient-to-br from-primary-500 to-primary-600 rounded-2xl shadow-lg animate-bounce-slow">
              <BookOpen className="h-8 w-8 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-gray-300 bg-clip-text text-transparent">
                Labs & Classrooms
              </h1>
              <p className="text-gray-600 dark:text-gray-400 mt-1">
                {isAdmin()
                  ? 'Manage and monitor all lab facilities'
                  : 'Explore facilities and report equipment issues'
                }
              </p>
            </div>
          </div>
          {isAdmin() && (
            <button className="btn-primary px-6 py-3 text-base font-semibold shadow-xl hover:shadow-2xl hover:shadow-primary-500/25">
              <Plus className="h-5 w-5 mr-2" />
              Add New Lab
            </button>
          )}
        </div>

        {/* Stats Summary */}
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-3 gap-4">
          <div className="flex items-center space-x-3 p-3 bg-gradient-to-r from-blue-50 to-blue-100 dark:from-blue-900/20 dark:to-blue-800/20 rounded-lg">
            <div className="p-2 bg-blue-500 rounded-lg">
              <BookOpen className="h-4 w-4 text-white" />
            </div>
            <div>
              <p className="text-sm font-medium text-blue-700 dark:text-blue-400">Total Labs</p>
              <p className="text-lg font-bold text-blue-900 dark:text-blue-300">{labs.length}</p>
            </div>
          </div>
          <div className="flex items-center space-x-3 p-3 bg-gradient-to-r from-green-50 to-green-100 dark:from-green-900/20 dark:to-green-800/20 rounded-lg">
            <div className="p-2 bg-green-500 rounded-lg">
              <CheckCircle className="h-4 w-4 text-white" />
            </div>
            <div>
              <p className="text-sm font-medium text-green-700 dark:text-green-400">Normal Status</p>
              <p className="text-lg font-bold text-green-900 dark:text-green-300">{labs.filter(lab => lab.status === 'normal').length}</p>
            </div>
          </div>
          <div className="flex items-center space-x-3 p-3 bg-gradient-to-r from-red-50 to-red-100 dark:from-red-900/20 dark:to-red-800/20 rounded-lg">
            <div className="p-2 bg-red-500 rounded-lg">
              <AlertTriangle className="h-4 w-4 text-white" />
            </div>
            <div>
              <p className="text-sm font-medium text-red-700 dark:text-red-400">Issues Reported</p>
              <p className="text-lg font-bold text-red-900 dark:text-red-300">{labs.filter(lab => lab.status === 'issue').length}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="card-glass p-6">
        <div className="flex flex-col lg:flex-row gap-6">
          {/* Search */}
          <div className="flex-1">
            <label className="label mb-2 flex items-center">
              <Search className="w-4 h-4 mr-2 text-primary-500" />
              Search Labs
            </label>
            <div className="relative group">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400 group-focus-within:text-primary-500 transition-colors" />
              <input
                type="text"
                placeholder="Search by lab name..."
                className="input pl-12 w-full focus:ring-primary-500 focus:border-primary-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>

          {/* Type Filter */}
          <div className="lg:w-48">
            <label className="label mb-2 flex items-center">
              <Filter className="w-4 h-4 mr-2 text-primary-500" />
              Facility Type
            </label>
            <select
              className="input w-full focus:ring-primary-500 focus:border-primary-500"
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
            >
              <option value="all">All Facilities</option>
              <option value="Lab">Labs Only</option>
              <option value="Classroom">Classrooms Only</option>
            </select>
          </div>

          {/* Status Filter */}
          <div className="lg:w-48">
            <label className="label mb-2 flex items-center">
              <AlertTriangle className="w-4 h-4 mr-2 text-primary-500" />
              Status
            </label>
            <select
              className="input w-full focus:ring-primary-500 focus:border-primary-500"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">All Status</option>
              <option value="normal">Normal</option>
              <option value="issue">Has Issues</option>
            </select>
          </div>
        </div>

        {/* Active Filters Display */}
        {(searchTerm || typeFilter !== 'all' || statusFilter !== 'all') && (
          <div className="mt-4 flex flex-wrap items-center gap-2 pt-4 border-t border-gray-200 dark:border-dark-600">
            <span className="text-sm font-medium text-gray-700 dark:text-gray-300">Active filters:</span>
            {searchTerm && (
              <span className="badge-info flex items-center">
                <Search className="w-3 h-3 mr-1" />
                "{searchTerm}"
              </span>
            )}
            {typeFilter !== 'all' && (
              <span className="badge-info">
                {typeFilter}
              </span>
            )}
            {statusFilter !== 'all' && (
              <span className={statusFilter === 'normal' ? 'badge-success' : 'badge-danger'}>
                {statusFilter === 'normal' ? 'Normal' : 'Has Issues'}
              </span>
            )}
            <button
              onClick={() => {
                setSearchTerm('');
                setTypeFilter('all');
                setStatusFilter('all');
              }}
              className="text-sm text-gray-500 hover:text-gray-700 dark:hover:text-gray-300 underline"
            >
              Clear all
            </button>
          </div>
        )}
      </div>

      {/* Labs Grid */}
      {filteredLabs.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredLabs.map((lab) => (
            <LabCard key={lab._id} lab={lab} />
          ))}
        </div>
      ) : (
        <div className="card text-center py-16">
          <div className="mx-auto w-20 h-20 bg-gradient-to-br from-gray-100 to-gray-200 dark:from-dark-700 dark:to-dark-600 rounded-2xl flex items-center justify-center mb-6">
            <AlertTriangle className="h-10 w-10 text-gray-400 dark:text-gray-500" />
          </div>
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
            {searchTerm || typeFilter !== 'all' || statusFilter !== 'all'
              ? 'No labs match your search'
              : 'No labs available'
            }
          </h3>
          <p className="text-gray-600 dark:text-gray-400 mb-6 max-w-md mx-auto">
            {searchTerm || typeFilter !== 'all' || statusFilter !== 'all'
              ? 'Try adjusting your search terms or clearing the filters to see more results.'
              : 'There are currently no labs or classrooms registered in the system.'
            }
          </p>
          {(searchTerm || typeFilter !== 'all' || statusFilter !== 'all') && (
            <button
              onClick={() => {
                setSearchTerm('');
                setTypeFilter('all');
                setStatusFilter('all');
              }}
              className="btn-primary"
            >
              Clear Filters
            </button>
          )}
        </div>
      )}

      {/* Issue Modal */}
      {showIssueModal && selectedLab && (
        <IssueModal
          lab={selectedLab}
          onClose={() => setShowIssueModal(false)}
          onSubmit={handleIssueSubmitted}
        />
      )}
    </div>
  );
};

export default LabsPage;
